<? ini_set('display_errors', '0');header("content-type: text/xml"); $phone = $_GET["cid"];$p1 = $phone[0] . $phone[1] . $phone[2];$p2 = $phone[3] . $phone[4] . $phone[5];$p3 = $phone[6] . $phone[7] . $phone[8] . $phone[9];include 'includes/config.php';function string_to_xml( $xml ){/* I am making sure that all & are not a part of a symbol */	$xml = str_replace( "&lt;" ,"<" ,  $xml );	$xml = str_replace( "&gt;" ,">" ,  $xml );	$xml = str_replace( "&quot;" , '"' ,  $xml );	$xml = str_replace( "&apos;" ,"'" ,  $xml );// Now i replace everything so it is in xml format and can be outputed without errors	$xml = str_replace( "&" , "&amp;" , $xml );	$xml = str_replace( "<" , "&lt;" , $xml );	$xml = str_replace( ">" , "&gt;" , $xml );	$xml = str_replace( '"' , "&quot;" , $xml );	$xml = str_replace( "'" , "&apos;" , $xml );return $xml;}$sql = "SELECT * FROM `tbl_user_information` LEFT JOIN `tbl_user` ON tbl_user_information.user_id = tbl_user.user_id WHERE `phone` LIKE '$p1%$p2%$p3'";$datarr = sql_to_arr($sql , "webapp");$data = $datarr[0];if( $data["status"] == "Y" ){ $status = "Active"; } else { $status = "Inactive"; }?><root><?$html = "<div style='width:590px; height:75px'><font style='color: green; font-size: 50px;' >User Info</font></div><table width='590px' style='width:590px;' ><tr><td>First Name</td><td>" . $data["first_name"] . "</td><td>Last Name</td><td>" . $data["last_name"] . "</td></tr><tr><td>Position</td><td>" . $data["position_applied"] . "</td><td>Status</td><td>" . $status . "</td></tr></table>";echo string_to_xml( $html );?></root>